const crypto = require("crypto");

function getTodaySeed() {
  const today = new Date();
  return today.getFullYear() * 10000 + (today.getMonth() + 1) * 100 + today.getDate();
}

function makeId() {
  return crypto.randomBytes(4).toString("hex");
}

