      "Statistics": [{q:"Mean is",options:{A:"Middle value",B:"Average of all values",C:"Most frequent value",D:"Range"},correct:"B",solution:"Mean: sum of values divided by count"},{q:"Median is",options:{A:"Average",B:"Most frequent",C:"Middle value when arranged",D:"Highest value"},correct:"C",solution:"Median: middle value in sorted order"},{q:"Mode is",options:{A:"Average",B:"Most frequent value",C:"Range",D:"Variance"},correct:"B",solution:"Mode: value occurring most frequently"},{q:"Range =",options:{A:"Highest - Lowest",B:"Average",C:"Sum",D:"Count"},correct:"A",solution:"Range: difference between highest and lowest"},{q:"Standard deviation measures",options:{A:"Average",B:"Spread of data",C:"Total",D:"Frequency"},correct:"B",solution:"Standard deviation: measure of data dispersion"},{q:"Variance is",options:{A:"Square root of SD",B:"Square of SD",C:"Average of data",D:"Range"},correct:"B",solution:"Variance = (Standard Deviation)²"},{q:"In a frequency distribution, class width is",options:{A:"Highest value",B:"Difference between class limits",C:"Number of values",D:"Middle value"},correct:"B",solution:"Class width: upper limit - lower limit"},{q:"Frequency is",options:{A:"Sum",B:"Average",C:"Number of times value occurs",D:"Range"},correct:"C",solution:"Frequency: count of occurrences"},{q:"Mean of 2, 4, 6, 8, 10 is",options:{A:"6",B:"5",C:"7",D:"4"},correct:"A",solution:"Mean = (2+4+6+8+10)/5 = 30/5 = 6"},{q:"Median of 1, 2, 3, 4, 5 is",options:{A:"2",B:"3",C:"4",D:"5"},correct:"B",solution:"Median (odd count): middle value = 3"}],
      "Probability": [{q:"Probability of event is",options:{A:"Favorable outcomes",B:"Total outcomes",C:"Favorable/Total outcomes",D:"Number of trials"},correct:"C",solution:"Probability = (Favorable outcomes)/(Total outcomes)"},{q:"Probability range is",options:{A:"0 to 1",B:"0 to 10",C:"-1 to 1",D:"1 to 100"},correct:"A",solution:"Probability: 0 ≤ P(E) ≤ 1"},{q:"Impossible event has probability",options:{A:"0",B:"1",C:"0.5",D:"∞"},correct:"A",solution:"Impossible: P(E) = 0"},{q:"Certain event has probability",options:{A:"0",B:"1",C:"0.5",D:"2"},correct:"B",solution:"Certain: P(E) = 1"},{q:"Probability of heads in coin toss is",options:{A:"0",B:"0.5",C:"1",D:"0.25"},correct:"B",solution:"Fair coin: P(heads) = 1/2 = 0.5"},{q:"Probability of rolling 3 on dice is",options:{A:"1/6",B:"1/3",C:"1/2",D:"1"},correct:"A",solution:"Fair dice: P(3) = 1/6"},{q:"For two events A and B, P(A or B) =",options:{A:"P(A) + P(B)",B:"P(A) + P(B) - P(A and B)",C:"P(A) × P(B)",D:"P(A) - P(B)"},correct:"B",solution:"Addition rule: P(A∪B) = P(A) + P(B) - P(A∩B)"},{q:"Independent events mean",options:{A:"Both certain",B:"One doesn't affect other",C:"Always impossible",D:"Always equal"},correct:"B",solution:"Independent: P(A and B) = P(A) × P(B)"},{q:"Complementary event probability",options:{A:"Always 0.5",B:"1 - P(E)",C:"P(E) - 1",D:"2×P(E)"},correct:"B",solution:"Complement: P(not E) = 1 - P(E)"},{q:"Probability of at least one head in 2 coin tosses is",options:{A:"0.25",B:"0.5",C:"0.75",D:"1"},correct:"C",solution:"P(at least 1 head) = 1 - P(no heads) = 1 - 1/4 = 3/4"}]
