      "आत्मकथ्य": [{q:"आत्मकथ्य का अर्थ क्या है?",options:{A:"दूसरे की कथा",B:"अपनी कथा स्वयं कहना",C:"कल्पित कथा",D:"ऐतिहासिक कथा"},correct:"B",solution:"आत्मकथ्य का अर्थ अपनी जीवन गाथा स्वयं कहना है"},{q:"हजारीप्रसाद द्विवेदी की रचना कौन सी है?",options:{A:"रामचरितमानस",B:"पंचपक्षी",C:"अशोक के फूल",D:"आत्मकथ्य"},correct:"D",solution:"आत्मकथ्य हजारीप्रसाद द्विवेदी की रचना है"},{q:"आत्मकथ्य में लेखक ने किस बारे में बात की है?",options:{A:"दूसरों का जीवन",B:"अपने जीवन के विभिन्न अनुभव",C:"कल्पित घटनाएं",D:"इतिहास"},correct:"B",solution:"लेखक अपने व्यक्तिगत जीवन और अनुभवों को साझा करते हैं"},{q:"द्विवेदी जी का जन्म कहाँ हुआ?",options:{A:"उत्तर प्रदेश",B:"बिहार",C:"बंगाल",D:"महाराष्ट्र"},correct:"A",solution:"हजारीप्रसाद द्विवेदी का जन्म उत्तर प्रदेश में हुआ"},{q:"आत्मकथ्य कैसी रचना है?",options:{A:"कविता",B:"नाटक",C:"गद्य",D:"संवाद"},correct:"C",solution:"आत्मकथ्य गद्य विधा में लिखी गई रचना है"},{q:"आत्मकथ्य का मुख्य उद्देश्य क्या है?",options:{A:"मनोरंजन",B:"आत्मविश्लेषण और जीवन अनुभव साझा करना",C:"आलोचना",D:"धर्म प्रचार"},correct:"B",solution:"आत्मकथ्य में लेखक अपने जीवन का विश्लेषण करते हैं"},{q:"द्विवेदी जी की रचनाएं किस क्षेत्र में योगदान देती हैं?",options:{A:"विज्ञान",B:"साहित्य और संस्कृति",C:"राजनीति",D:"व्यापार"},correct:"B",solution:"उनकी रचनाएं हिंदी साहित्य और संस्कृति में महत्वपूर्ण हैं"},{q:"आत्मकथ्य का प्रकार क्या है?",options:{A:"काल्पनिक",B:"वास्तविक और व्यक्तिगत",C:"ऐतिहासिक",D:"धार्मिक"},correct:"B",solution:"आत्मकथ्य व्यक्तिगत और वास्तविक अनुभवों पर आधारित है"},{q:"इस रचना में किन विषयों पर चर्चा होती है?",options:{A:"केवल राजनीति",B:"व्यक्तिगत संघर्ष, शिक्षा, सांस्कृतिक विकास",C:"केवल धर्म",D:"केवल इतिहास"},correct:"B",solution:"आत्मकथ्य में लेखक के संघर्ष, सीख और विकास की चर्चा होती है"},{q:"आत्मकथ्य की भाषा कैसी है?",options:{A:"कठिन और तकनीकी",B:"सरल, मधुर और प्रवाहमान",C:"अति जटिल",D:"केवल संस्कृत शब्दों से भरी"},correct:"B",solution:"आत्मकथ्य की भाषा सरल, मधुर और पठनीय है"}],
